package 연습;

public class 연습14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//실수형 (1.0 , 0.0123 , 0.01234)소수점
		//실수형에 예약어 float , double
		
		float name  =0.5F;//4바이트 (float)
		float name2 =0.5f;//대소문자 표현 가능
		
		double name3 =0.123; //8바이트(double)자바에 기본 실수형타입 
		double name4 =1.123456;
		
		System.out.println(name);
		System.out.println(name2);
		System.out.println(name3);
		System.out.println(name4);
		System.out.println("================");
		System.out.println(0.5f);
		System.out.println(0.555);
		

	}

}
