package 연습;

public class 연습17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//상수(constant)
		
		final int MAX_NUM =12; 
		//상수는 final을 무조건 써야된다.
		//상수는 한번 값을 (초기화)넣으면 바꿀수 없다 
		//MAX_NUM = 100;
		
		final int MIN_NUM;//상수 선언
		MIN_NUM = 0; //상수에 값을 초기화(값을 넣다)
		System.out.println(MIN_NUM);
		
		final int MY_COUNT = 100;
		//1.위에서 상수는 어디를 말하는 걸까요?
		//MY_COUNT
		//2.예약어는 어디를 말하는 걸까요?
		//final
		//3.리터럴은 어디를 말하는 걸까요?
		//100
		
		
		

	}

}
