package 연습;

public class 연습5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 10;
		//부호 연산자
		System.out.println(-num);
		
		System.out.println(num);
		//실제 값이 변경된 것은 아니다.
		//값을 변경 하려면 대입 연산자를 사용해야 한다.
		num = -num;
		System.out.println(num);
	
	}

}
