package 연습;

public class 연습7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 5;
		int num2 = 3;
		//논리형  값
		boolean value = (num1 > num2);
		System.out.println(value);
		
		System.out.println(num1 < num2);
		//num2 보다 num1이 작다 fales 
		System.out.println(num1 >=num2);
		//num2 보다 num1이 크거나 같다 true
		System.out.println(num1 <= num2);
		//num2 보다 num1이 작거나 같다 false
		System.out.println(num1 ==num2);
		//num2 이랑 num1이 같다 false
		System.out.println(num1 != num2);
		//num2 이랑 num1이 다르다 true
		
	}

}
