package 연습1;

public class For문1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//num 같이 반복 2부터 9까지
		for(int num =2;num <= 9;num++) {
			
			//i값이 반복 1부터 9까지
		for(int i =1; i<=9; i++) {
			System.out.println(num + "*" + i + "=" + (num*i));
		}
		
		
		
		}//end of for
	}

}
